package overview;

public class IfSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x,y;
		
		x=10;
		y=20;
		
		if(x<y)
			System.out.println("X is less than Y");
		
		x=x*2;
		if(x==y)
			System.out.println("X is now equal to Y");
		
		x=x*2;
		if(x>y)
			System.out.println("X now greater than Y");

	}

}
